    this.panZoomObject = Panzoom(stall, { canvas: true });
    // panzoom.zoom(2);
    this.placeWallTitleElement();
    this.getTestPosts();
  },
  methods: {
    placeWallTitleElement() {
      let xCenter = window.innerWidth / 2;
      let yCenter = window.innerHeight / 2;

      let stallTitleElement = document.getElementById("stall-title");

      let stallTitleElementWidth = stallTitleElement.offsetWidth;
      let stallTitleElementHeight = stallTitleElement.offsetHeight;

      let stallTitleElementLeft = xCenter - stallTitleElementWidth / 2;
      let stallTitleElementTop = yCenter - stallTitleElementHeight / 2;

      stallTitleElement.style.left = stallTitleElementLeft + "px";
      stallTitleElement.style.top = stallTitleElementTop + "px";
    },
    getTestPosts() {
      for (var i = 0; i < 1000; i++) {
        let postObject = {
          text: `Test post ${i}`,
          xOffset: this.getRandomValue(),
          yOffset: this.getRandomValue(),
        };
        this.stallPosts.push(postObject);
      }
    },
    getRandomValue() {
      return (
        Math.ceil(Math.random() * 10000) * (Math.round(Math.random()) ? 1 : -1)
      );
    },
  },
